     /// <summary>
        ///     Helper method to determine if the given object
        ///     is in the visual sub tree of the Details Presenter.
        /// </summary>
        private bool IsInVisualSubTree(DependencyObject visual)
        {
            while (visual != null)
            {
                if (visual == this)
                {
                    return true;
                }
                if (visual is Visual || visual is System.Windows.Media.Media3D.Visual3D)
                visual = VisualTreeHelper.GetParent(visual);
                else
                    return false;

            }
            return false;
        }
