OK, here's my xaml and code-behind:

<Window x:Class="WpfApplication5.Window1"
    xmlns="http://schemas.microsoft.com/winfx/2006/xaml/presentation"
    xmlns:x="http://schemas.microsoft.com/winfx/2006/xaml"
    xmlns:Toolkit="clr-namespace:Microsoft.Windows.Controls;assembly=WPFToolkit"
    Title="Window1" Height="300" Width="300" Loaded="Window_Loaded">
    <Grid>
        <StackPanel>
            <Toolkit:DataGrid x:Name="MyDataGrid" ItemsSource="{Binding}" />
        </StackPanel>
    </Grid>
</Window>

CodeBehind:
using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Windows;
using System.Windows.Controls;
using System.Windows.Data;
using System.Windows.Documents;
using System.Windows.Input;
using System.Windows.Media;
using System.Windows.Media.Imaging;
using System.Windows.Navigation;
using System.Windows.Shapes;
using System.Data;

namespace WpfApplication5
{
    /// <summary>
    /// Interaction logic for Window1.xaml
    /// </summary>
    public partial class Window1 : Window
    {
        public Window1()
        {
            InitializeComponent();
        }

        private void LoadData()
        {
            DataTable dt = new DataTable();
            for (int c = 1; c < 6; c++)
            {
                dt.Columns.Add(new DataColumn(string.Format("Column {0}", c.ToString())));
            }

            for (int d = 1; d < 6; d++)
            {
                string[] values = new string[5];
                for (int c = 0; c < 5; c++)
                {
                    values[c] = string.Format("Row {0} Column {1}", d.ToString(), c.ToString());
                }
                dt.Rows.Add(values);
            }
            MyDataGrid.DataContext = dt;
        }

        private void Window_Loaded(object sender, RoutedEventArgs e)
        {
            LoadData();
            MyDataGrid.SelectedIndex = 2;
        }
    }
}
