        private bool ProcessDatePickerKey(KeyEventArgs e)
        {
            switch (e.Key)
            {
                case Key.System:
                {
                    switch (e.SystemKey)
                    {
                        case Key.Down:
                        {
                            if ((Keyboard.Modifiers & ModifierKeys.Alt) == ModifierKeys.Alt)
                            {
                                TogglePopUp();
                                return true;
                            }
 
                            break;
                        }
                    }
 
                    break;
                }
 
                case Key.Enter:
                {
                    SetSelectedDate();
                    return true;
                }
            }
 
            return false;
        }
