  public class MainRibbon : Ribbon
  {
    private RibbonTabPanel _ribbonTabPanel;

    public override void OnApplyTemplate()
    {
      this._ribbonTabPanel = base.GetTemplateChild("PART_TABSHOST") as RibbonTabPanel;
      base.OnApplyTemplate();
    }

    protected override void OnPreviewMouseWheel(System.Windows.Input.MouseWheelEventArgs e)
    {
      if (!this.IsMinimized && (this.SelectedTab != null))
      {
        int index = this._ribbonTabPanel.Children.IndexOf(this.SelectedTab);
        if (e.Delta < 0)
        {
          if (index < (this._ribbonTabPanel.Children.Count - 1))
          {
            RibbonTab curRibbonTab = this._ribbonTabPanel.Children[index + 1] as RibbonTab;
            while (curRibbonTab.Visibility == System.Windows.Visibility.Hidden || curRibbonTab.Visibility == System.Windows.Visibility.Collapsed)
            {
              index += 1;
              if (index > this._ribbonTabPanel.Children.Count - 1) return;
              curRibbonTab = this._ribbonTabPanel.Children[index] as RibbonTab;
            }
            this.SelectedTab = curRibbonTab;
          }
        }
        else if (index > 0)
        {
          RibbonTab curRibbonTab = this._ribbonTabPanel.Children[index - 1] as RibbonTab;
          while (curRibbonTab.Visibility == System.Windows.Visibility.Hidden || curRibbonTab.Visibility == System.Windows.Visibility.Collapsed)
          {
            index -= 1;
            if (index < 0) return;
            curRibbonTab = this._ribbonTabPanel.Children[index] as RibbonTab;
          }
          this.SelectedTab = curRibbonTab;
        }
        e.Handled = true;
      }
    }
  }



